float HeartFunction(float x, float y){
	return 5.0 * x * x - 6.0 * abs(x) * y + 5.0 * y * y;
}

bool TestFunction(float x, float y){
	bool judge = false;
    if(x > 0.0f && y > 0.0f)
        judge = true;
    return judge;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
   vec2 uv = fragCoord/iResolution.xy;

    vec2 CenterOfScreen = iResolution.xy / 2.0;
    float scale = abs(20.0 * sin(iTime));
    if(scale == 0.0f)
        scale = 0.0001f;
    vec2 position = (fragCoord.xy - CenterOfScreen) / scale;
    vec2 vector = (fragCoord.xy - CenterOfScreen) / min(iResolution.x, iResolution.y);

    vec3 bcol = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
   // if(TestFunction(position.x, position.y))
      //  bcol = vec3(1.0,0.0,0.0);

    if(HeartFunction(position.x, position.y) <= 128.0f)
        bcol = vec3(1.0, 0.0,0.0)*(1.0 - length(vector));

   	fragColor = vec4(bcol, 1.0);
}
