void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   float mx = fragCoord.x / iMouse.x;
   float my = fragCoord.y / iMouse.y;

    vec2 uv = vec2( fragCoord.xy - 0.5*iResolution.xy );
	uv = 2.0 * uv.xy / iResolution.y;

	vec3 backgroundColor = vec3(1.0);
	vec3 color1 = vec3(0.5, 0.5, 1.0);
	vec3 color2 = vec3(1.00, 0.0, 0.0);
	vec3 color3 = vec3(0.0, 0.0, 1.0);

	vec3 pixel = backgroundColor;

    if( mod(uv.x, 0.1) < 2.0 / iResolution.y ) pixel = color1;
    if( mod(uv.y, 0.1) < 2.0 / iResolution.y ) pixel = color1;

    vec2 center;
    vec2 d;

    float speed = 4.0;

    for(float i = 0.0;i < 1.0;i += 0.05)
    {
        center = vec2(sin(iTime * i * speed) * 0.5, 
                      cos(iTime * i * speed) * 0.5 );
        d = uv - center;

		if( length(d) < 0.09) 
        {
		    pixel = vec3(6.0 / i * .05 , 0.0, 0.0);

		}
    }


	fragColor = vec4(pixel, 1.0);

}
