#define PI 3.14159265359

float xor(vec2 p)
{
    float f = 0.;
    for (int i = 0; i < 8; ++i)
    {
        p = 2. * fract(p);
        f *= 2.;
        f += mod(floor(p.x) + floor(p.y), 2.);
    }
    return f / 256.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy / .5 - 1.;
    uv.x *= iResolution.x / iResolution.y;

    float f = cos(length(uv) * PI) + sin(iTime) * 2.;
    f = xor(vec2(f, atan(uv.x, uv.y) / PI * 6.) + iTime);
    if (length(uv) > 1.) f = 1. - f;
    f *= sin(length(uv) * PI - PI / 2.);

    fragColor = vec4(f, f, f, 1.0);
}
