#define RADIUS 0.05
vec2 uv;
vec4 col;
void drawCircle( vec2 center, float radius )
{
	vec2 dif = center - uv;
	if( length(dif)*length(dif) < radius*radius )
	{
	col = vec4(1.0);
	}
}

vec2 findCenter()
{
	vec2 start = vec2(0.5,0.5); 
	float y = -0.25;
	float secs = floor(iTime);	
	float dec = fract( iTime );
	float restant = fract( iTime * 0.5 );
	if( restant >= 0.5  )
	{
		y = 0.25 - (restant-0.5);
	}
	else
	{
		y = -0.25 + restant;
	}
		return start + vec2(y);	
}

bool pincharCirculo(vec2 center, float radius)
{
    if(iMouse.z>0.0)
    {
        vec2 p=iMouse.xy/iResolution.xy;
        if (distance(center, p) <= radius)
        {
            return true;
        }
        }
return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	col = vec4(0);
	uv = fragCoord.xy / iResolution.xy;
	vec2 center = findCenter();
	if (!pincharCirculo(center, RADIUS))
		drawCircle( center, RADIUS );

	fragColor = col;
}
