void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    float r = 0.85, b = 0.5, w = 0.98;
    vec3 col = vec3(w);
    if (uv.x<-0.8 && uv.y>0.25) col = vec3(r,0,0);    else
    if (uv.x>-0.3 && uv.y>0.25) col = vec3(r,0,0);    else
    if (uv.x<-0.8 && uv.y<-0.25) col = vec3(r,0,0);    else
    if (uv.x>-0.3 && uv.y<-0.25) col = vec3(r,0,0);    else
    if (uv.x>-0.7 && uv.x<-0.4) col = vec3(0,0,b);    else
    if (uv.y>-0.15 && uv.y<0.15) col = vec3(0,0,b);
    fragColor = vec4(col, 1.0);
}
