void mainImage( out vec4 f, in vec2 p )
{
    float n = 0.;
    vec2 c = vec2(-.745,.186) + 3. * (p.xy/iResolution.y-.75)*pow(.01,1.+cos(4 * iTime)*3.), z=c*n;

    for( int i=0; i<1920; i++ )
    {
        z = vec2( z.x*z.x - z.y*z.y, 2.*z.x*z.y ) + c;

        if( dot(z,z)>1e4 ) break;

        n++;
    }

    f = .5 + .5*cos( vec4(30,40,110,0) + .05*(n - log2(log2(dot(z,z)))) );
}
