void mainImage (out vec4 fragColor, in vec2 fragCoord )

{
    vec2 COORDINATES = -1. + 2. *(fragCoord.xy/iResolution.xy);
    COORDINATES.x *= iResolution.x/iResolution.y;
    COORDINATES.y -= 0.2;
    float ANGLE = abs (atan (COORDINATES.x, COORDINATES.y) / 3.14);
    float RADIUS = length (COORDINATES);
    float SHAPE = (13. * ANGLE - 22. * ANGLE * ANGLE + 10. * ANGLE * ANGLE * ANGLE)/ (6. - 5.* ANGLE); 
    vec3 HCOLOR = mix (vec3 (1, 1, 0.7), vec3 (1, 0, .25) + .3 * cos ( iTime), smoothstep (-0.01, 0.01, SHAPE - RADIUS) );
    fragColor = vec4 (HCOLOR, 1);
}
