void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0,fragCoord / iResolution.xy);
}

#define runFor 4000.0
//change define to run for N frames before restarting

vec4 read(vec2 fragCoord, vec2 pos)
{
    return texture(iChannel0,(fragCoord+pos)/iResolution.xy);
}

float circ(vec2 fragCoord, vec2 iRes, float rad)
{
    float val = 1.0f - length(fragCoord - 0.5*vec2(iRes.xy))*0.008;
    return tanh(val*10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor=texture(iChannel0, fragCoord/iResolution.xy); 

    if(iFrame<4 || mod(float(iFrame),runFor) ==0.0)
    {
        fragColor=texture(iChannel1, fragCoord/iResolution.xy)*circ(fragCoord, iResolution.xy, 500.0); 

    }
    else
    {
		vec4 u = vec4(0.0,0.0,0.0,0.0);
    	float spillAmount = clamp(iTime*0.01,0.0,0.7);

        float samp1 = length(texture(iChannel0, fragCoord/iResolution.xy));
        float samp2 = length(texture(iChannel2, fragCoord/iResolution.xy));
        float samp3 = length(texture(iChannel3, fragCoord/iResolution.xy));

        float ang1 = (samp1)*3.14*2.0;
        float x1 = cos(ang1);
        float y1 = sin(ang1);

        float ang2 =samp2*3.14*2.0;

        //rotate!
        ang2 = ang2 + samp2 + samp3*0.3 + samp2*iTime*2.0  ;

        float x2 = cos(ang2);
        float y2 = sin(ang2);        

        u+=read(fragCoord,vec2(x1,y1)*spillAmount);
        u+=read(fragCoord,vec2(x2,y2)*spillAmount*8.0)*3.0;
		u+=read(fragCoord,vec2(0.0,0.0));

        u/=5.0;
        fragColor = u;
    }
}
