void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 cpos = vec2(5.0, 2.5) * (uv - vec2(0.5));

    float r = sqrt(cpos.x * cpos.x + cpos.y * cpos.y);

    cpos = cpos * pow(r, 0.7);

    uv = cpos / vec2(5.0, 2.5) + vec2(0.5);

    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.5*(fragCoord/iResolution.xy*2.0 - vec2(1.0,1.0));

    float dt = uv.x - float(int(uv.x));
    float x1 = 0.5 - abs(uv.x > 0.0 ? 0.5 - dt : 0.5 + dt);
    float rd = 1.0-abs(0.25-(x1*x1 + 0.25*uv.y*uv.y))*5.0;
    fragColor = vec4(1.0-4.0*abs(abs(uv.y) - 1.0),0,rd,0);
}
