vec3 hsv2rgb( in float h ) {
    return clamp( abs(mod(h*6.0+vec3(0,4,2),6.0)-3.0)-1.0, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 no = uv*2.0-1.0;
    no *= dot(no, no);

    vec3 a = texture(iChannel0, uv-no*0.006-0.016).rrr;
    vec3 b = texture(iChannel0, uv-no*0.012-0.012).rrr;
    vec3 c = texture(iChannel0, uv-no*0.018-0.008).rrr;

    float baseHue = iTime*0.1;

    vec3 colA = hsv2rgb(baseHue+0.0/3.0);
    vec3 colB = hsv2rgb(baseHue+1.0/3.0);
    vec3 colC = hsv2rgb(baseHue+2.0/3.0);

    float len = length(colA+colB+colC);
    vec3 col = (colA*a + colB*b + colC*c)/len*sqrt(3.0);
    fragColor = vec4(col, 1);
}
