// "ShaderToy Tutorial Part 2 - Building stuff with circles" 
// by Martijn Steinrucken aka BigWings/CountFrolic - 2016
//
// This shader is part of an ongoing tutorial series on how to ShaderToy
// For an explanation go here: 
// https://www.youtube.com/watch?v=GgGBR4z8C9o

float Circle(vec2 uv, vec2 p, float r, float blur) {

    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy; // 0 <> 1.

    uv -= .5;           //-0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);

    float mask = Circle(uv, vec2(0.), .4, .05);

    mask -= Circle(uv, vec2(-.13, .2), .07, .01);
    mask -= Circle(uv, vec2(.13, .2), .07, .01);

    float mouth = Circle(uv, vec2(0., 0.), .3, .02);
    mouth -= Circle(uv, vec2(0., 0.1), .3, .02);

    mask -= mouth;

    col = vec3(1., 1., 0.)*mask;

    fragColor = vec4(col, 1.0);
}
