float random (vec2 uv) 
{ 
    	return fract(sin(dot(uv.xy,
                             vec2(1234.56, 5678.233)))
                     		* 1234.0349575);
}

// makes it random 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    	vec2 uv = fragCoord.xy / iResolution.xy; 
    	vec3 c1 = texture(iChannel0, uv).rgb; 
    	vec3 c2 = texture(iChannel1, uv).rgb; 

    float v = 0.5 * (1.0 + sin(iTime)); //0-1 Range 
    v += random(uv);  //0-2 Range 
    v *= 0.5; // 0-1 Range 
    if (v > 0.5) fragColor.rgb = c1;
    else fragColor.rgb = c2;
    fragColor.a = 1.;

}
