// Sunflower Transform Tunnel - written 2015-12-02 by JT
// (a modification of https://www.shadertoy.com/view/Mdd3R7)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Thanks to FabriceNeyret2 for optimizations!
void mainImage( out vec4 O, vec2 I )
{
	vec2 R = iResolution.xy;
    I = (I+I-R)/R.y;
    //I = (I+I-(I=iResolution.xy))/I.y; // NOT COMPATIBLE
    I /= dot(I,I);
    I = vec2(0, length(I)) + atan(I.y, I.x) / 6.283;
    I.x = ceil(I.y) - I.x;
    //I.x *= I.x * (2.*(sqrt(5.)-1.));
    I.x *= I.x * 2.4; // approximation to golden angle pi*(3-sqrt(5))
    O -= O + smoothstep(.4, .5, length(fract(I)-.5)) - 1.;
    O *= smoothstep(.0, .1, abs(fract(I.y)-.5));
}
