float fbm(vec2 p)
{
    p /= 75.;
    mat2 m = mat2(.8, -.6, .6, .8); 
    float amp = .5;

    float f=0.;
    for (int i = 0; i < 6; i++)
    {
        f += amp * texture(iChannel0, p).r;
        p *= m * 2.;
        amp *= .5;
    }

    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;

    vec2 p = uv;//vec2(atan(uv.y,uv.x)*3.1415*1., dot(uv,uv)+iTime);
    p.y -= iTime;
    float v = fbm(p);

	fragColor = vec4(v, 0., v*.3, 1.);
}
