vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}


vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad*2.0;
	float t = atan(clamp(d, 0.0, 4.0));
	return vec4(color, 1.0-t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy*0.5;
	float radius = 0.25 * iResolution.y;

    // Background layer
	vec4 layer1 = vec4(rgb(0.0, 0.0, 0.0), 0.5);



    //Mic Reaction
    float react;
    react = texture(iChannel0,vec2(0.1,0.1)).x;


    //Visualizer Line
    vec2 av = 4.0 * (fragCoord.xy/iResolution.xy) - 2.0;
	float spec = 2.0*texture( iChannel0, vec2(0.1,0.1)).x;
    float col = 0.0;

    //av.y += sin(iTime * normalize(react) + av.x*1.0)*spec; //Horizontal Visualizer Line
    //col += abs(0.066/av.y) * spec;
    av.x += sin(iTime * normalize(react) + av.y*1.0)*spec;   //Vertical Visualizer Line
    col += abs(0.066/av.x) * spec;   


	vec4 layer4 = vec4(col,col,col,1.0);


	// Circle
	vec3 white = rgb(255.0, 255.0, 255.0);
    vec3 red = rgb(255.0, 0.0, 0.0);
	vec4 layer2 = circle(uv,center*1.5,radius* react, red*react*2.0);
   	vec4 layer3 = circle(uv,center*0.5,radius* react, white*react);

	// Blend the layers
	vec4 L1 = mix(layer1, layer2,react );
    vec4 L2 = mix(L1, layer3,layer1.a );
    vec4 L3 = mix(L2, layer4, L2.a);

	fragColor = L3;
}
