// variant of https://www.shadertoy.com/view/4dtfWr

void mainImage(out vec4 O, vec2 U) {

    float t = iTime;
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),    // ray direction
          p = 30./q;                          // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                 // rotation
        t = max(abs(q.z)-6.,length(q.xy)-4.), // bounding cylinder
        t = max(t, abs(sin(atan(q.y,q.x)-q.z))             *min(1.,length(q.xy)) - .1  ), // helix
     // t = max(t, abs(mod(atan(q.y,q.x)-q.z,6.28)-3.14)/3.*min(1.,length(q.xy)) - .02 ), // helix
        p += t*D;                             // step forward = dist to obj
}
