void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uvOri = uv;


    uv.x += sin((uvOri.y ) * 1.1) * 0.00012 ;
    uv.y += sin((uvOri.x ) * 14.1) * 0.0012 ;    
    uv.x += sin((uvOri.y ) * 51.1) * 0.000512 ;
    uv.y += sin((uvOri.x ) * 51.1) * 0.000512 ;

   	vec2 offse = vec2(sin(iTime * 2.) ,sin(iTime * 1.4) ) * 0.4;
    offse -= -0.5;

    vec4 tex = texture(iChannel0,uv ); 

    float circle = step (length(uv - offse ) ,.04) * 0.9999;




   	vec4 Colored = circle * texture(iChannel1,vec2(iTime * 0.01));

    fragColor =  (tex * 0.979) + (Colored) ;
}
