void mainImage( out vec4 c,vec2 f )
{
	vec2 v = f/iResolution.xy;
    float l = mix(max(abs(v.x-.5), abs(v.y-.5)), length(v-.5), mod(iDate.w,10.) /10.);
    c=pow(texture(iChannel0, mod((v+l*iDate.w)/l, 1.))*l, vec4(1./2.2));
}

void mainImage( out vec4 c, vec2 f )
{
    vec2 v = f/iResolution.xy;
    c = vec4(sin(v.x), sin(1.-v.x), cos(v.y+v.x), 1.)*mod(vec4(iDate.w, 1.-iDate.w,iDate.w, 1.-iDate.w),1.);
}
