//float t = 0.1;
float w = 0.05;
vec2 center = vec2(0.5, 0.5);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;
    float maxEdge = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord.xy / maxEdge;

    center *= iResolution.xy  / maxEdge ;

    float g = 1.5;
    vec4 gamma = vec4(g, g, g, 1.0);

    vec4 color0 = pow(texture(iChannel0, uv), gamma);
    vec4 color1 = texture(iChannel1, uv);

    float duration = 2.0;

    float R = length(0.5 * iResolution) / maxEdge;

    float t = mod(float(iTime), duration) /  duration;

    float correction = mix(w, -w, t);

    t *= R;

    float d = distance(uv, center);

    float choose = smoothstep(t  - w, t + w, d + correction); // clamped ramp

    fragColor = mix(color1, color0, choose); // lerp
}
