#define PI 3.145
#define TAU 2.*3.145

vec2 rot(vec2 p, float theta)
{
	return vec2(cos(theta) * p.x - sin(theta) * p.y,
                sin(theta) * p.x + cos(theta) * p.y);
}

float ring(vec2 p, float r, float t)
{
    return smoothstep(r - t, r, length(p)) + (1.- smoothstep(r - t - t, r - t, length(p)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2. * uv -1.0; 
    uv.y *= iResolution.y / iResolution.x;

    float r = 0.15;
    float t = 0.01;
    float k = 2. * r - t;
    float time = iTime;

    uv *= 1.0 + exp(length(uv));
    uv = rot(uv, -0.1*time);
    uv += 2.*iMouse.xy / iResolution.xy ;
    uv = mod(uv, vec2(3.*k));
    uv -= vec2(1.5*k);

    r = 0.10 + 0.05*sin(iTime);
 	k = 2. * r - t;

    float d1 = ring(uv, r, t);
    float d2 = ring(uv + k * vec2(cos(time),sin(time)), 0.15, 0.01);
    float d3 = ring(uv + k * vec2(cos(time + TAU*1./6.),sin(time + TAU*1./6.)), 0.15, 0.01);
    float d4 = ring(uv + k * vec2(cos(time + TAU*2./6.),sin(time + TAU*2./6.)), 0.15, 0.01);
    float d5 = ring(uv + k * vec2(cos(time + TAU*3./6.),sin(time + TAU*3./6.)), 0.15, 0.01);
    float d6 = ring(uv + k * vec2(cos(time + TAU*4./6.),sin(time + TAU*4./6.)), 0.15, 0.01);
    float d7 = ring(uv + k * vec2(cos(time + TAU*5./6.),sin(time + TAU*5./6.)), 0.15, 0.01);

	float d = d1;
    d = min(d, d2);
    d = min(d, d3);
    d = min(d, d4);
    d = min(d, d5);
    d = min(d, d6);
    d = min(d, d7);

	fragColor = vec4(d,d,d,1.0);
}
