float trig( float dist, float decay, float frequency, float amplitude, float speed )
{

	return exp(-decay * dist) *  
    sin(dist * frequency + ((iTime) * speed)) * amplitude;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( -iResolution.xy + 2.0 * fragCoord ) / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    // Time varying pixel color

    vec2 mou = ( iMouse.xy / iResolution.xy );

    float wave = texture( iChannel1, vec2( 0.0, 0.74 ) ).x;

    float dist = length( uv - mou );
    float distO = length( uv + mou );
    float tri = dist * distO;

    uv += trig( tri + wave, 0.01, 3.0, 1.0, 0.5 );

    vec3 col = texture( iChannel0, uv ).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
