// variant of https://shadertoy.com/view/XdtyRs


#define T ( 1.- abs( 1.-2.* texture( iChannel0, f*p.xzw/3. ).y ) ) / ( f += f )

void mainImage(out vec4 O, vec2 U) {
    vec4 p, d = vec4(.8,0,U/iResolution.y-.8);
    O = vec4(.5,0,.8,0) - d.w;
    for( float f, t = 2e2 ; --t > 0. ; p = .02*t*d )
        p.xz += .5*iTime,
        f = .5, f = T+T+T+T -p.w-1.5 ,
        f > 0. ? O += ( (1.-O) - 2.*f*vec4(.5,1,1,0) ) *f *.5: O; }
