void mainImage( out vec4 fColor, in vec2 fCoord )
{
    vec2 res = iResolution.xy; 
    vec2 f = fCoord.xy;
    vec2 uv = f / res; 
    float m = 0.5; 
// left
    if( uv.x < m){
        f = fCoord.xy;
        res = vec2(res.x * 0.5, res.y);
        vec2 res2 = f/res; 
        vec4 texColor = 1.0 * texture(iChannel0,res2);


    	if(texColor.r < 0.5 && texColor.b < 0.4 && texColor.g > 0.5)
    	{
    	 	texColor = texture(iChannel1,res2);   
    	}

    	fColor = texColor;
    	}
    	// right side
    else{
        f = vec2(f.x - res.x * 0.5, f.y); 
        res = vec2(res.x * 0.5, res.y); 
        vec2 res2 = f/res; 
        //mirror effect
        res2.x = 1.-res2.x; 
        vec4 texColor = 2.0 * texture(iChannel0,res2);

    	if(texColor.r < 0.2 && texColor.b < 0.9 && texColor.g > 0.1) 
    	{
    	 	texColor = 3.0 * texture(iChannel1,res2); 
    	}
    	fColor = texColor;  	
    }
}
