#define METHOD 2
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 a = fragCoord.xy / iResolution.xy;

	vec3 bg = texture( iChannel0, a ).xyz;
	vec3 cg = texture( iChannel1, a ).xyz;


    float maxrb = max( cg.r, cg.b );
    float k = clamp( (cg.g-maxrb)*5.0, 0.0, 1.0 );

#if METHOD==1

	float ll = length( cg );
    cg.g = min( cg.g, maxrb*0.8 );
    cg = ll*normalize(cg);

#else    

    float dg = cg.g; 
    cg.g = min( cg.g, maxrb*0.8 ); 
    cg += dg - cg.g;

#endif
    a.x = a.x / iResolution.x; 
    a.y = a.y / iResolution.y;

    if (a.y < 0.5) a.y = 1.0 - a.y;





    vec3 s = texture(iChannel0, a).xyz ;

     vec3 ss = texture(iChannel1,a).xyz ;

   fragColor = vec4( mix(ss*cg, s*bg, k), 1.0);
}
