void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 nppd = 1.0/iResolution.xy * 1.0;

    vec2 nmp = iMouse.xy / iResolution.xy;

    vec3 c = vec3(0.0);

    vec3 tl = texture(iChannel0, uv + nppd * vec2( -1.0, -1.0)).rgb;
    vec3 tc = texture(iChannel0, uv + nppd * vec2(  0.0, -1.0)).rgb;
    vec3 tr = texture(iChannel0, uv + nppd * vec2(  1.0, -1.0)).rgb;
    vec3 ml = texture(iChannel0, uv + nppd * vec2( -1.0,  0.0)).rgb;
    vec3 mc = texture(iChannel0, uv + nppd * vec2(  0.0,  0.0)).rgb;
    vec3 mr = texture(iChannel0, uv + nppd * vec2(  1.0,  0.0)).rgb;
    vec3 bl = texture(iChannel0, uv + nppd * vec2( -1.0,  1.0)).rgb;
    vec3 bc = texture(iChannel0, uv + nppd * vec2(	0.0,  1.0)).rgb;
    vec3 br = texture(iChannel0, uv + nppd * vec2(  1.0,  1.0)).rgb;

    vec3 t = tl+tc+tr+ml+mc+mr+bl+bc+br;
    c = t/9.0;


    fragColor = vec4( c, 1.0 );
}
