#define pi 3.14159
#define tau (2.*pi)

float sdCircle( in vec2 p, in float r ) {
	return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (2.*fragCoord - iResolution.xy)/iResolution.y;

    vec3 color = vec3(0);
    float a = abs(atan(p.y, p.x))/pi;
    float w0 = texture(iChannel0, vec2(a, 0)).x;
    float d0 = (abs(sdCircle(p, .652))-.15*w0)*40.;
    color += mix(vec3(.98,.76,.67), vec3(.25,.86,.89), w0*.75)
                     *(1.-fract(d0))*exp(-.46*d0);

    float w1 = texture(iChannel0, vec2(p.x*.5+.5, 1)).x;
    float d1 = p.y-.5*(w1-.5);

    color.r += .78*float(abs(d1)<0.005);

    fragColor.rgb = color;
}
