void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //mid grey background + small noise for contouring artefacts 
  	float background = .1 + .8*pow(uv.x, 2.2);
    background += texture(iChannel0, iResolution.xy/256.*uv).x/255.;

    vec4 boxlims = vec4(0.45, 0.55, 0.4, 0.6); //xmin, xmax, ymin, ymax;
    boxlims.xy += 0.4*sin(iTime*2.);

    vec2 box = step(boxlims.xz, uv) - step(boxlims.yw, uv);

    fragColor = vec4(box.x * box.y == 1. ? .2 : background);
}
