// variant of https://shadertoy.com/view/4stcRM

vec2 M = vec2(1.275/2.,1), v, R;

#define S(v) smoothstep(30./R.y,0.,abs(v))
#define d .11

void D(inout vec4 O, vec2 U) {
    U = 2.*fract(U)-1.; U.y -= d; U /= M;         // local frame
    O.r += max(0.,1.-length(U));                  // red disk

    float a = atan(U.x,U.y), l=length(U), e=.628; // --- draw pentagon
    a = mod(a+e,e+e)-e;                           // 5-fold symmetry
    U = l * vec2(cos(a),sin(a));
    O += S(U.x-.885);                             // draw 1 side
}

void mainImage( out vec4 O, vec2 U )
{
    U -= .5; O-=O;
    R = iResolution.xy;

    U *= M *8./R.y ;

    U.x+=.25;
    D(O,U);                                       // even columns
    U.x+=.5; U.y = .47+d-U.y; 
    D(O,U);                                       // odd columns


}
