#define mi 256.

float
mb( vec2 c, float r2 ) {

    vec2
    z = c,
    d = z * z;

    for( float i = 0.; i < mi; ++ i ) {

        if( r2 < d.x + d.y )

            return i;

        z = vec2( d.x - d.y, 2. * z.x * z.y ) - c;

        d = z * z;
    }

    return mi;
}

void
mainImage( out vec4 o, in vec2 i ) {

    float
    c = cos( .3 * iTime ),
    s = sin( 2.115 * iTime );

    mat2
	r = mat2( c, -s, s, c );

    c = log2( mb( vec2( 1.39005, 0.114548 ) + ( 2. * i.xy / iResolution.xy - 1. ) * vec2( iResolution.x / iResolution.y, 1. ) * 10. * r * pow( 1e-6, ( .5 + .5 * sin( .2 * iTime ) ) ), mi * .1 * ( 1. + sin( 3. * iTime ) * cos( .5 * iTime ) ) ) ) * 64.,
    s = dot( sign( vec2( -dFdy( c ), dFdx( c ) ) ), vec2( 1. ) );

    o = vec4( clamp( .5 * ( s + 1. ) * ( c / mi ) * vec3( 1., .6, .4 ), 0., 1. ), 1. );
}
