#define R 0.3
#define r 0.01
#define pi 3.1415

float kolo(vec2 _r, vec2 _c, float _R)
{
	float d = length(_r-_c) - _R;
	return(smoothstep(1.0,0.0,120.0*d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.x*=(iResolution.x/iResolution.y);
	float t = iTime*2.0;	
	vec2 c=vec2(1.66/2.0,0.5);
	vec2 p1= c+vec2(-R+r+(R-r+sin(t)*(R-r)),0);
	vec2 p2= c+vec2(0.0,sin(t+0.5*pi)*(R-r));
	vec2 p3 = p1 + 0.5*(p2-p1);
	float col = kolo(uv,c,R);

	for(float phi=0.0; phi<=2.0*pi; phi+=0.25*pi)
	{
		vec2 p1c = p1-p3;
		vec2 p1r;
		p1r.x = cos(phi)*p1c.x-sin(phi)*p1c.y;
		p1r.y = sin(phi)*p1c.x+cos(phi)*p1c.y;
		col *= (1.0-kolo(uv, p1r+p3 ,r));
	}
	fragColor = vec4(col,col,col,1.0);
}
