vec2 center = vec2(0.5,0.5);
float speed = 1.5;
float RippleSize = 0.5;
float BorderSize =  0.05;
const int Ripples = 10;


float circle(vec2 uv, float diameter, float EdgeSize)
{


	return 1.0-smoothstep(0.0,EdgeSize,abs(length(uv)-diameter));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / min(iResolution.x, iResolution.y);
	float x = (center.x-uv.x);
	float y = (center.y-uv.y); // *invAr;

	uv.x -= center.x * (iResolution.x / iResolution.y);
	uv.y -= center.y;


	vec4 tex = texture(iChannel0, uv).rgba;

	float z = 0.0;

	float r = sqrt(x*x + y*y);

	float v = 0.0;

	for (int i = 0; i < Ripples ; i++)
	{
		float t = (iTime * speed) - ( float(i) * RippleSize);

		if (t > 0.0)
		{
			v = circle(uv, t, BorderSize) * ( 1.0 - t);
			z += v;
		}


	}

	tex *= 1.0 - z;

	fragColor = tex;
}
