// http://en.wikipedia.org/wiki/Lyapunov_fractal

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const float N = 2500.0;
	const int iterations=int(N);

	vec2 uv = fragCoord.yx / iResolution.yx;  // Note the xy to yx swizzle

	float G=abs( sin(iTime));

	uv*=vec2( 4.0-G*3.4, 4.0-G*3.0);
	uv+=vec2( G*3.4, G*2.4);

	float x = 0.5;
	float runningTotal = 0.0;
	float Rn = 0.0;

	for( int i=0; i < iterations; i++){

		if( mod( float(i), 12.0) < 6.0){   // Periodic function AAAAAA BBBBBB
			Rn = uv.x;
		}
		else{
			Rn = uv.y;
		}
		x = Rn * x * ( 1.0 - x);

		runningTotal += log( abs( Rn * (1.0 - 2.0 * x)));
	}

	float lambda = 1.0 / N * runningTotal;

	float r = lambda<0.0 ? 0.9 - sqrt( abs(lambda*0.3)): 0.0;
	float g = lambda<0.0 ? 0.9 - sqrt( abs(lambda*0.6)): 0.0;
	float b = lambda<0.0 ? 0.0 : abs(lambda*3.0-0.1);
	fragColor = vec4( r, g, b, 1.0);
}
