void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	float time = mod(iTime,16.0);
	float type = fract(time/4.0) > 0.5 ? 1.0 : 0.0;
	float shades = 2.0 + floor(time/4.0);
	if (type==0.0) {
		// variant 1: bars
		fragColor = vec4(
			(shades/(shades-1.0))*mod(floor(shades*uv.x)/shades, 1.0),
			(shades/(shades-1.0))*mod(floor(shades*shades*uv.x)/shades, 1.0),
			(shades/(shades-1.0))*mod(floor(shades*shades*shades*uv.x)/shades, 1.0),
			1.0);
	} else {
		// variant 2: blocks
		fragColor = vec4(
			(shades/(shades-1.0))*mod(floor(shades*uv.x)/shades, 1.0),
			(shades/(shades-1.0))*mod(floor(shades*shades*uv.x)/shades, 1.0),
			(shades/(shades-1.0))*mod(floor(shades*uv.y)/shades, 1.0),
			1.0);
	}
}
