void mainImage( out vec4 o,  vec2 u )
{
    u /= iResolution.xy;
    float y = sin(iTime*.3),
          s = length(u-vec2(.5,y));
    o = max(mix(vec4(.2,.4,.6,1)*(.6+y*.4),//sky
            vec4(.95,.5,.4,1), (1.-abs(y))*(1.-u.y)-pow(s,2.)), //horizon scattering
            vec4(0,0,.1,1))+ //night
        max(0.,10.-s*200.);//sun
}
