void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * uv;
    p *= iResolution.xy / min(iResolution.x,iResolution.y);

    p.x += 0.1*sin(iTime)* sin(p.y*12.0);
    p.y += 0.1*cos(iTime)* sin(p.x*15.0);

    float d = abs(.8-length(p));
    float a = atan(p.y,p.x);

    d += 0.2*sin(a*15.);    
    a += sin(iTime/5.0);

    fragColor = vec4(1.0-abs(d*sin(a)),1.0-abs(d*sin(a)), 1.0-abs(d*sin(a)), 1.);
}
