float crossf(vec2 uv) {
	return 1.-abs(abs(uv.x-.5)-abs(uv.y-.5));
}

float dist(vec2 uv) {
	return sqrt(pow(abs(uv.x-.5),2.)+ pow(abs(uv.y-.5),2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float raw_music = texture(iChannel0,vec2(1.,1.)).x*.1 - iTime*.2;
	float d = dist(uv);

	float r = abs(-1.+mod(raw_music+d,2.));
	r = pow(abs(-1.+mod(raw_music+d,2.)),4.);
	float g = texture(iChannel1, vec2(uv.x+r/3., 1.-uv.y)).g;
	float b = abs(-1.+mod(raw_music+d+2./3.,2.));

	fragColor = vec4(r,g,crossf(uv),1.0);
}
