vec4 hsv_to_rgb(float h, float s, float v, float a)
{
	float c = v * s;
	h = mod((h * 6.0), 6.0);
	float x = c * (1.0 - abs(mod(h, 2.0) - 1.0));
	vec4 color;

	if (0.0 <= h && h < 1.0) {
		color = vec4(c, x, 0.0, a);
	} else if (1.0 <= h && h < 2.0) {
		color = vec4(x, c, 0.0, a);
	} else if (2.0 <= h && h < 3.0) {
		color = vec4(0.0, c, x, a);
	} else if (3.0 <= h && h < 4.0) {
		color = vec4(0.0, x, c, a);
	} else if (4.0 <= h && h < 5.0) {
		color = vec4(x, 0.0, c, a);
	} else if (5.0 <= h && h < 6.0) {
		color = vec4(c, 0.0, x, a);
	} else {
		color = vec4(0.0, 0.0, 0.0, a);
	}

	color.rgb += v - c;

	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float width = iResolution.x;
    float height = iResolution.y;
    float millis = iTime * 1.3;
	float ms = millis * 0.25;
	int panel_w = 10;
	int panel_h = 6;
	float sub_w = width / float(panel_w);
	float sub_h = height / float(panel_h);
	float cx = width / 2.0;
	float cy = height / 2.0;

	int grid_x = int(fragCoord.x / sub_w);
	int grid_y = int(fragCoord.y / sub_h);
	float shift_x = sin(ms) + 1.0;
	float rx = shift_x - (float(grid_x)/float(panel_w));
	float ry = float(grid_y)/float(panel_h);

	if(rx*ry > 0.1){
		fragColor = hsv_to_rgb(sin(ms)/2.0 + 0.5, rx * ry, 1.0, 1.0);
	}else{
		fragColor = hsv_to_rgb(sin(ms+0.1)/2.0 + 0.5, 0.1, 1.0, 1.0);
	}
}
