// logistic map
// http://en.wikipedia.org/wiki/Logistic_map
// r varies with time
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float r=4.0*sin(0.5*iTime);
	vec2 p1 = 2.0*fragCoord.xy/iResolution.xy-0.5;
	vec2 p2 = p1;

	float hit = 3.0;
	for(int i=0;i<25;i++)
	{
		p1=r*p1*(1.0-p1);

		float d=distance(p1,p2);
		if (d<hit)
		    hit=d;
	}
	hit=5.0*clamp(hit,0.0,0.2);
	fragColor = vec4(hit,hit,hit,1.0);
}
