float shoulderStrenght = 0.22;
float linearStrenght = 0.3;
float linearAngle = 0.1;
float toeStrenght = 0.2;
float toeNumerator = 0.01;
float toeDenominator = 0.30;
float linearWhitePointValue = 11.2;

float Tonemapping(float x ){    
    float calc;
    calc = ((x*(shoulderStrenght*x + linearAngle*linearStrenght) + toeStrenght*toeNumerator)/(x*(shoulderStrenght*x+linearStrenght)+toeStrenght* toeDenominator))- toeNumerator/ toeDenominator;                        
    return calc;    
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord.xy / iResolution.xy; 
    fragColor = texture ( iChannel0, uv);
    if ( fragCoord.x > iResolution.x/2.0){
   	fragColor.xyz = vec3(Tonemapping(fragColor.r)/Tonemapping(linearWhitePointValue),
                        Tonemapping(fragColor.g)/Tonemapping(linearWhitePointValue),
                        Tonemapping(fragColor.b)/Tonemapping(linearWhitePointValue));   
    }   
}
