float alterDistance(vec2 v1, vec2 v2, float strength, float speed) 
{
  return sin(dot(normalize(v1), normalize(v2)) * strength + speed * iTime) / 100.0;
}

float alterDistance(vec2 uv, vec2 center) 
{
  vec2 diff = center - uv;
  float len = length(diff);

  len += alterDistance(diff, vec2(0.0, 1.0), 10.0, 2.0);
  len -= alterDistance(diff, vec2(1.0, 0.0), 10.0, 2.0);

  return len;
}

float limit(float x, float min, float max)
{
    if (x < min)
        x = 0.0;

    if (x > max)
        x = 1.0;

    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = texture(iChannel0, uv);

    vec2 center = vec2(0.5, .5); 

    vec2 p = ceil(uv * 300.0) / 300.0;

  	float d = alterDistance(center, p);

    vec4 pink = vec4(0.89, 0.0, 0.69, 1.0);

    float r1 = 0.18;
    float r2 = 0.225;
    float r3 = 0.3;

    if (d <= r1)
    {
        fragColor =  color;
    }
    else if (d <= r2)
    {
        fragColor = color * smoothstep(0.25, r1, d);
    }
    else
    {
        float t = iTime * 0.075;

        float cloud1 = texture(iChannel1, p + vec2(t, t)).r + 0.1 * sin(t * 10.0);
        float cloud2 = texture(iChannel1, 0.7 * p + vec2(-t, t)).r - 0.1 * sin(t * 10.0);

        float cloud3 = mix(cloud1, cloud2, 0.5);
        if (d <= r3)
            cloud3 = min(1.0, cloud3 + smoothstep(r3, 0.14, d));
        float cloud = step(0.6, cloud3) * 0.6;
        if (cloud < 0.01) 
            cloud = step(0.4, cloud3) * 0.4;
        if (cloud < 0.01) 
            cloud = step(0.3, cloud3) * 0.2;  

      fragColor = cloud * pink;
    }
}
