float rand(vec2 uv)
{

	return (fract(sin(dot(uv, vec2(12., 70.)))*1000.));

}

// Value noise
float noise(in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

float createPolygon(int sides, vec2 uv, float ratio, float x, float y, float rotate)
{
    vec2 coor = vec2(x, y);
    uv -= vec2(coor.x *ratio, coor.y);
    float a = atan(uv.x,uv.y)+ 3.14159265359 + rotate;
	float r = 6.28318530718 / float(sides);
    float dist = cos(floor(.5+a/r)*r-a)*length(uv);

    return dist;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv *= ratio;
    uv *= 20.0;
    float value = step(15.0 + noise(uv + (iTime) * 2.0), uv.y) - step(15.5 + noise(uv + (iTime) * 2.0), uv.y);
    value += step(17.0 + noise(uv + (iTime) * 2.0), uv.y) - step(17.5 + noise(uv + (iTime) * 2.0), uv.y);
    value += step(13.0 + noise(uv + (iTime) * 2.0), uv.y) - step(13.5 + noise(uv + (iTime) * 2.0), uv.y);
    value += (1.0 - step(7.0, createPolygon(3, uv + noise(uv + iTime), ratio, 15.5, 16.0, 0.52)));
	fragColor = vec4(vec3(value), 0.0);
}
