//pulled from https://www.shadertoy.com/view/Md23DV
//minor changes to change disk size and behavior

#define PI 3.1415926
#define TWOPI 6.2831
#define sat(x) clamp(x, 0., 1.)

//create a 2-D disk on the screen
void disk(vec2 r, vec2 center, float radius, vec3 color, inout vec3 pixel) {
	if( length(r-center) < radius) {
		pixel = color;
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse -= 0.5;
    uv -= 0.5;
	vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y; //move coordinates to center of frame

	vec3 bgCol = vec3(0.3);

	vec3 col1 = vec3(0.216, 0.471, 0.698); // blue
	vec3 col2 = vec3(1.00, 0.329, 0.298); // yellow
	vec3 col3 = vec3(0.867, 0.910, 0.247); // red

	vec3 pixel = bgCol;

	float x = 0.2*cos(iTime*5.0);
    float radius = 0.2 + 0.1*sin(iTime*2.0);
	float y = 0.3*cos(iTime*5.0 + PI/2.0);

	disk(r, vec2(x, y), radius, col3, pixel);
	disk(r, mouse, 0.2, col1, pixel);

    x = y * y * 2.;

	disk(r, vec2(x, y * 2.), .11, vec3(x,x,x), pixel);



	fragColor = vec4(pixel, 1.0);
}
