#define PI 3.14159265359
#define E 2.71828182845

float dotv(vec2 v)
{
    return dot(v.x, v.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    vec2 nnv = nuv * vec2(aspect, 1.0);
    float t = iTime;

    float bpm = 1.0 / 60.0 * 69.0;
    float m0 = texture(iChannel0, vec2(0.0, 0.25)).x;
    float m1 = texture(iChannel0, vec2(0.3, 0.25)).x;
    float bt = pow(abs(sin(t * bpm * 2.0)), 4.0);

    m0 -= 0.4;
    m0 = pow(m0, 2.5) * 4.0;
    m1 = pow(m1, 2.0) * 2.0;

    float r = sin(t * 2.0) * 0.5 + t * 1.5;
    mat2 rm = mat2(cos(r), -sin(r), sin(r), cos(r));

    nnv = rm * nnv;
    nnv *= (1.0 - pow(length(nnv), 1.0) * 2.0 + sin(t) * 2.0 * bt + m1);

    float c0 = sin(nnv.y * 8.0) * sin(nnv.x * 8.0 + t);
    float c1 = sin(c0 * 8.0 + t * 4.0 + m0 * 2.0);
	float c2 = sin(c0 * c1 + m1);

    float inner = 0.15 / pow(length(nnv), 1.1);

    fragColor.x = max(0.0, c0) * pow(c1, 4.0);
    fragColor.yz = vec2(c0 * c1);
    fragColor.z += pow(fragColor.x * c2, 0.5) * 20.0;

    fragColor.x *= m0 * m1 + bt * m1 * 12.0;
    fragColor.z += fragColor.x * pow(c1, 2.0);

}
