#define WIDTH 45.0
#define HEIGHT 22.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float v0 = 1. - uv.x * uv.y;
	float v1 = uv.x;
	float v = mix(v0, v1, iMouse.x / iResolution.x);
	float scale = 0.1 * (v * v * v * iResolution.x) / iResolution.x;

	float s = pow(1. - clamp(scale * 10., 0., 1.), 3.);

    const float blockScale = 0.02;
	fragColor = texture(iChannel0, mix(floor(uv / blockScale) * blockScale, uv, s) + 0.5);
}
