void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((100.0 + 0.*pow(iTime / 5., 0.9))) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    float value =  pow(sin(uv.x) * uv.y + sin(uv.y) * uv.x, 0.5);
    float color = sin(value + iTime / 1.0) * 3.0;
    //color = color * sign(uv.x) * sign(uv.y); // revision 2

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;
    if(color > 0.0) {
	  fragColor = vec4(0., high, 0.,1.0);
    } else {
      fragColor = vec4(high, high, 0.,1.0);
    }
}
