vec4 Rect(vec2 uv, vec2 pos, float width, float height, vec4 color, out bool isThis)
{
    if((uv.x > (pos.x - (width / 2.0))) && (uv.x < (pos.x + (width / 2.0))) &&
       (uv.y > (pos.y - (height / 2.0))) && (uv.y < (pos.y + (height / 2.0))))
    {
        isThis = true;
        return color;
    }

    return vec4(0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    vec2 posOne = vec2(iResolution.x / 2.0, iResolution.y - 20.0);
    //vec2 posOne = iMouse.xy; 
    vec2 posTwo = vec2(20.0, 20.0);
    vec2 posThree = vec2(iResolution.x - 20., 20.0);

    vec4 nodeColor = vec4(1.0, 0.5, 0.5, 1.0);

    vec2 lerpPosOne = vec2(mix(posOne, posTwo, 0.5+0.5*(sin(iTime))));
    vec2 lerpPosTwo = vec2(mix(posThree, posOne, 0.5+0.5*(sin(iTime))));
    vec2 lerpPosThree = vec2(mix(lerpPosTwo, lerpPosOne, 0.5+0.5*(sin(iTime))));

    vec4 final = vec4(0.0);

    bool checker = false;
    vec4 layerOne = Rect(uv, posOne, 50.0, 50.0, vec4(1.0), checker);
    if(checker)
        final = layerOne;

    checker = false;
    vec4 layerTwo = Rect(uv, posTwo, 50.0, 50.0, vec4(1.0), checker);
	if(checker)
        final = layerTwo;

    checker = false;
    vec4 layerThree = Rect(uv, posThree, 50.0, 50.0, vec4(1.0), checker);
	if(checker)
        final = layerThree;

    checker = false;
    vec4 layerFour = Rect(uv, lerpPosOne, 30.0, 30.0, nodeColor, checker);
	if(checker)
        final = layerFour;

    checker = false;
    vec4 layerFive = Rect(uv, lerpPosTwo, 30.0, 30.0, nodeColor, checker);
	if(checker)
        final = layerFive;

    checker = false;
    vec4 layerSix = Rect(uv, lerpPosThree, 30.0, 30.0, vec4(0.0, 1.0, 1.0, 1.0), checker);
	if(checker)
        final = layerSix;

    fragColor = final;
}
