#define E (n=n.zxy, d(p+n)-d(p-n))
#define t normalize(vec3

float d(vec3 p)
{
    return 9. - abs(p.y) - texture(iChannel0, p.xz*.01).x * 7.;
}

void mainImage( out vec4 f, vec2 g )
{
    vec3 
        n = iResolution,
        r = t(g+g-n.xy,n.y))*.4,
    	p=n-n;

    p.z = iTime * 9.;

    for (int i=70;i-->0;p+=r*d(p))
    	f.rgb = t(n)) + d(p - t(E,E,E)));
}
