// .44 ~= 7/16 (the texture offset for greek letters and math symbols)
#define T texture(iChannel0, vec2(z, x/16.) + .44).xxxx

void mainImage(out vec4 c, vec2 u)
{
    // Set up UV coordinates and get the aspect ratio right
    vec3 R = iResolution;
    u = (R.xy - u - u) / R.y;

    float t = iTime,
          // Map UV space to an animated spiral
          y = atan(u.x, u.y) - t,
          z = y*.159,  // .159 ~= 0.5 / PI
          x = fract(log(length(u)) + z) * 2. -1.;

    // Make a shaded spiral that sorta looks 3d
    c = x * sin(y) *
        // Make animated radial lines for maximum mesmerization
        pow(sin(y*16.+ t*18.)+1., .1) -
        // Make the drop-shadow for the letters
        T*.2;
    // Sepia tone color makes it look old and mysterious
    c.x += .2;
    // Shift the texture offset for the drop shadow.
    x -= .1;
    // Draw the white foreground letters
    c += T;
}
