#define T(x) texture(iChannel0,x).r

void mainImage( out vec4 a, vec2 b )
{
    float k = cos(iTime/4.)*.5+.5;

	vec2 c = b / iResolution.xy + k;

    a = vec4(T(c),T(c*c),T(c*k),1.);

    float d = mod(
        cos(iTime-c.y*2.+T(vec2( c.x/2.0 , k )))
      , c.x);

    a *= pow( T(vec2(.2)) ,4.)*6.;

    a = tanh( abs( vec4(k,c.x,d,1)*pow(d*k,.2) - a*3. ) );

}
