void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float rnd = fract(sin(dot(fragCoord.xy ,vec2(12.9898,78.233))) * 43758.5453);
    rnd = step(rnd, 1.2*iMouse.x / iResolution.x-0.1);
    vec3 col0 = texture(iChannel0, uv).rgb;
    vec3 col1 = texture(iChannel1, uv).rgb;
    fragColor = vec4(mix(col0, col1, rnd), 1.0);
}
